    private String obtenerColor() {
        String color="";
        double azar = Math.random();
        
        if(azar<0.33){
            color = "Rojo";
        }
        if(azar>=0.33&&azar<0.66){
            color = "Verde";
        }
        
        if(azar>=0.66){
            color="Amarillo";
        }
                  
        return color;
    }
    
    private void pintar( String color){
                   
        if ("Rojo".equals(color)) {
            radioRojo.setEnabled(true);
            radioAmarillo.setEnabled(false);
            radioVerde.setEnabled(false);
            
            radioRojo.setForeground(Color.red);
            radioAmarillo.setForeground(Color.GRAY);
            radioVerde.setForeground(Color.GRAY);
        }
        if ("Amarillo".equals(color)) {
            radioRojo.setEnabled(false);
            radioAmarillo.setEnabled(true);
            radioVerde.setEnabled(false);
            
            radioAmarillo.setForeground(Color.yellow);
            radioRojo.setForeground(Color.GRAY);
            radioVerde.setForeground(Color.GRAY);
        }
        if ("Verde".equals(color)) {
            radioRojo.setEnabled(false);
            radioAmarillo.setEnabled(false);
            radioVerde.setEnabled(true);
            
            radioVerde.setForeground(Color.green);
            radioRojo.setForeground(Color.GRAY);
            radioAmarillo.setForeground(Color.GRAY);
        }

        if (!"Verde".equals(color) && !"Amarillo".equals(color) && !"Rojo".equals(color)) {
            radioRojo.setEnabled(false);
            radioAmarillo.setEnabled(false);
            radioVerde.setEnabled(false);
            
            radioVerde.setForeground(Color.GRAY);
            radioRojo.setForeground(Color.GRAY);
            radioAmarillo.setForeground(Color.GRAY);
        }
    }
